import { Flex, Icon, Image, Tooltip } from "@chakra-ui/react";
import { replaceIpfsUrl } from "lib/sdk";
import { AiFillStar, AiOutlineStar } from "react-icons/ai";
import { Text } from "tw-components";

export function WalletButton(props: {
  name: string;
  subtitle?: string;
  icon: string;
  isChecked: boolean;
  onSelect: () => void;
  recommended?: boolean;
  onRecommendedClick?: () => void;
}) {
  const { isChecked, onSelect, name, icon, subtitle } = props;
  return (
    <Flex
      borderRadius="lg"
      border="1px solid transparent"
      bg={isChecked ? "hsl(215.88deg 100% 60% / 15%)" : "inputBg"}
      _hover={
        !isChecked
          ? {
              bg: "inputBg",
              borderColor: "borderColor",
            }
          : {
              borderColor: "blue.500",
            }
      }
      transition="background 100ms ease, border-color 100ms ease"
      alignItems="center"
      userSelect={"none"}
      pr={1}
      gap={1}
    >
      <Flex
        gap={3}
        alignItems="center"
        flexGrow={1}
        p={2}
        onClick={() => {
          onSelect();
        }}
        cursor="pointer"
      >
        <Image width={7} height={7} alt={name} src={replaceIpfsUrl(icon)} />

        <Flex
          flexDir="column"
          justifyContent="center"
          flexGrow={1}
          alignSelf="stretch"
        >
          <Text
            fontWeight={500}
            fontSize={14}
            color={isChecked || subtitle ? "heading" : "paragraph"}
          >
            {name}
          </Text>
          {subtitle && (
            <Text fontSize={12} color="faded">
              {subtitle}
            </Text>
          )}
        </Flex>
      </Flex>

      <RecommendedIconButton
        onClick={props.onRecommendedClick}
        isRecommended={props.recommended}
      />
    </Flex>
  );
}

export function RecommendedIconButton(props: {
  isRecommended?: boolean;
  onClick?: () => void;
}) {
  return (
    <Tooltip
      hasArrow
      label={props.isRecommended ? "Recommended" : "Not Recommended"}
      placement="top"
    >
      <Flex
        justifyContent="center"
        alignItems="center"
        onClick={props.onClick}
        cursor="pointer"
        role="button"
        aria-label="Toggle Recommended"
      >
        <Icon
          as={props.isRecommended ? AiFillStar : AiOutlineStar}
          w={4}
          h={4}
          color={props.isRecommended ? "blue.500" : "faded"}
        />
      </Flex>
    </Tooltip>
  );
}

export function SocialButton(props: {
  name: string;
  icon: "email" | "google" | "facebook" | "apple" | "phone";
  onClick: () => void;
  isSelected: boolean;
}) {
  return (
    <Flex
      borderRadius="lg"
      border="1px solid transparent"
      bg={props.isSelected ? "hsl(215.88deg 100% 60% / 15%)" : "inputBg"}
      _hover={
        !props.isSelected
          ? {
              bg: "inputBg",
              borderColor: "borderColor",
            }
          : {
              borderColor: "blue.500",
            }
      }
      transition="background 100ms ease, border-color 100ms ease"
      alignItems="center"
      userSelect={"none"}
      pr={1}
      gap={1}
    >
      <Flex
        gap={3}
        alignItems="center"
        flexGrow={1}
        p={2}
        onClick={props.onClick}
        cursor="pointer"
      >
        <Image
          width={7}
          height={7}
          alt={props.name}
          src={socialIconMap[props.icon]}
        />

        <Text
          fontWeight={500}
          fontSize={14}
          color={props.isSelected ? "heading" : "paragraph"}
        >
          {props.name}
        </Text>
      </Flex>
    </Flex>
  );
}

