import { SmartContract } from "../../src/evm/contracts/smart-contract";
import {
  extendedMetadataMock,
  jsonProvider,
  signers,
  sdk,
  hardhatEthers,
} from "./before-setup";
import { SignerWithAddress } from "@nomiclabs/hardhat-ethers/signers";
import { expect } from "chai";
import { ethers, providers, utils } from "ethers";
import { mockUploadMetadataWithBytecode } from "./utils";
import {
  modularFactoryBytecode,
  modularFactoryCompilerMetadata,
} from "./mock/modularFactoryMetadata";
import {
  mockCoreInitializableBytecode,
  mockCoreInitializableCompilerMetadata,
  mockCoreInitializableDeployedBytecode,
} from "./mock/mockCoreInitializableMetadata";
import {
  mockExtensionWithFunctionsBytecode,
  mockExtensionWithFunctionsCompilerMetadata,
  mockExtensionWithFunctionsDeployedBytecode,
} from "./mock/mockExtensionWithFunctionsMetadata";
import { AddressZero } from "../../src/evm/constants/addresses/AddressZero";
import { compatibleExtensions } from "../../src/evm/common/modular/compatibleExtensions";
import { predictAddressDeterministic } from "../../src/evm";

describe("Modular contract deployment", async () => {
  let contract: SmartContract;
  let adminWallet: SignerWithAddress;
  let claimerWallet: SignerWithAddress;
  let notificationCounter: number;
  let transactionCount: number;

  async function mockPublishModularFactory() {
    const mockPublisher = process.env.contractPublisherAddress;
    process.env.contractPublisherAddress =
      "0xf5b896Ddb5146D5dA77efF4efBb3Eae36E300808";
    const publishUri = await mockUploadMetadataWithBytecode(
      "CloneFactory",
      modularFactoryCompilerMetadata.output.abi,
      modularFactoryBytecode,
      "",
      {
        ...extendedMetadataMock,
        deployType: "standard",
        networksForDeployment: {
          allNetworks: true,
          networksEnabled: [],
        },
        publisher: await adminWallet.getAddress(),
      },
      "ipfs://QmPeYVS1RXKRqYa1Xdh8yBNUiGF8ugPZcoBwU7udLfS85c/0",
    );

    process.env.contractPublisherAddress = mockPublisher;
  }

  async function mockPublishExtension() {
    const mockPublisher = process.env.contractPublisherAddress;
    process.env.contractPublisherAddress =
      "0xf5b896Ddb5146D5dA77efF4efBb3Eae36E300808";

    const publishUri = await mockUploadMetadataWithBytecode(
      "DemoExtensionWithFunctions",
      mockExtensionWithFunctionsCompilerMetadata.output.abi,
      mockExtensionWithFunctionsBytecode,
      "",
      {
        ...extendedMetadataMock,
        deployType: "standard",
        networksForDeployment: {
          allNetworks: true,
          networksEnabled: [],
        },
        publisher: await adminWallet.getAddress(),
      },
      "ipfs://QmZe9RARSDmDteX7eSxPP5CgwvGPST6BBF52QS8PhTpX3G/0",
    );

    process.env.contractPublisherAddress = mockPublisher;

    return publishUri;
  }

  async function mockPublishAndDeployMockCore() {
    const mockPublisher = process.env.contractPublisherAddress;
    process.env.contractPublisherAddress =
      "0xf5b896Ddb5146D5dA77efF4efBb3Eae36E300808";

    const publishUri = await mockUploadMetadataWithBytecode(
      "DemoCore",
      mockCoreInitializableCompilerMetadata.output.abi,
      mockCoreInitializableBytecode,
      mockCoreInitializableDeployedBytecode,
      {
        ...extendedMetadataMock,
        deployType: "autoFactory",
        routerType: "modular",
        defaultExtensions: [
          {
            extensionName: "DemoExtensionWithFunctions",
            extensionVersion: "",
            publisherAddress: await adminWallet.getAddress(),
          },
        ],
        factoryDeploymentData: {
          implementationAddresses: {},
          factoryAddresses: {},
          implementationInitializerFunction: "initialize",

          modularFactoryInput: { hooksParamName: "extensions" },
        },
        networksForDeployment: {
          allNetworks: true,
          networksEnabled: [],
        },
        publisher: await adminWallet.getAddress(),
      },
      "ipfs://QmfPo2npPjdeWjcQQDD6kjgELRFixcVn4sX3aQZgPC5XHs/0",
    );

    const walletAddress = await sdk.wallet.getAddress();

    const address = await sdk.deployer.deployContractFromUri(
      publishUri,
      [walletAddress, [], ["0x"]],
      {
        forceDirectDeploy: false,
        hooks: [
          {
            extensionName: "DemoExtensionWithFunctions",
            extensionVersion: "",
            publisherAddress: "0xFD78F7E2dF2B8c3D5bff0413c96f3237500898B3",
          },
        ],
      },
    );

    const core = await sdk.getContract(address);

    process.env.contractPublisherAddress = mockPublisher;

    return core;
  }

  before(async () => {
    [adminWallet, claimerWallet] = signers;
  });

  beforeEach(async () => {
    sdk.updateSignerOrProvider(adminWallet);
    await jsonProvider.send("hardhat_reset", []);

    await adminWallet.sendTransaction({to: "0x4c8D290a1B368ac4728d83a9e8321fC3af2b39b1", value: "1000000000000000000"})
    await adminWallet.provider?.sendTransaction(
      "0xf87e8085174876e800830186a08080ad601f80600e600039806000f350fe60003681823780368234f58015156014578182fd5b80825250506014600cf31ba02222222222222222222222222222222222222222222222222222222222222222a02222222222222222222222222222222222222222222222222222222222222222",
    );
    await adminWallet.sendTransaction({
      to: "0x7a0d94f55792c434d74a40883c6ed8545e406d12",
      data: "",
    });
    await adminWallet.sendTransaction({
      to: "0xcfa3a7637547094ff06246817a35b8333c315196",
      data: "",
    });
    await adminWallet.sendTransaction({
      to: "0x0000000000ffe8b47b3e2130213b802212439497",
      data: "",
    });
  });

  it("should deploy core and install hooks", async () => {
    await mockPublishModularFactory();
    await mockPublishExtension();
    const mockCore = await mockPublishAndDeployMockCore();

    const extensions = await mockCore.call("getInstalledExtensions");

    expect(extensions.length).to.equal(1);
    expect(extensions[0].implementation).to.not.equal(AddressZero);
  });

  // it("should check extension compatibility", async () => {
  //   const isCompatible = await compatibleExtensions(
  //     [mockExtensionERC20Bytecode, mockExtensionERC20Bytecode],
  //     11155111,
  //   );

  //   expect(isCompatible).to.be.true;
  // });
});
